ror = Arr::get($errors, 'content')) : ?>			<div class="it-error"><?php echo $error ?></div>		<?php endif ?>	</div>    <div class="it-row">		<label for="content-id"><?php echo t('news.content') ?></label>		<div class="it-textarea">            <textarea name="content" rows="15" class="it-editor" id="content-id"><?php echo Arr::get($_REQUEST, 'content') ?></textarea>        </div>    		<?php if ($error = Arr::get($errors, 'content')) : ?>			<div class="it-error"><?php echo $error ?></div>		<?php endif ?>	</div>    <div class="it-row">		<label for="h1-id"><?php echo t('news.h1') ?></label>		<div class="it-text"><input type="text" id="h1-id" name="h1" value="<?php echo Arr::get($_REQUEST, 'h1') ?>" /></div>		<?php if ($error = Arr::get($errors, 'h1')) : ?>			<div class="it-error"><?php echo $error ?></div>		<?php endif ?>	</div>    <div class="it-row">		<label for="alias-id"><?php echo t('news.alias') ?></label>		<div class="it-text"><input type="text" id="alias-id" name="alias" value="<?php echo Arr::get($_REQUEST, 'alias') ?>" /></div>		<?php if ($error = Arr::get($errors, 'alias')) : ?>			<div class="it-error"><?php echo $error ?></div>		<?php endif ?>	</div>    <div class="it-row">		<label for="title-id"><?php echo t('news.title') ?></label>		<div class="it-text"><input type="text" id="title-id" name="title" value="<?php echo Arr::get($_REQUEST, 'title') ?>" /></div>		<?php if ($error = Arr::get($errors, 'title')) : ?>			<div class="it-error"><?php echo $error ?></div>		<?php endif ?>	</div>    <div class="it-row">		<label for="keywords-id"><?php echo t('news.keywords') ?></label>		<div class="it-text"><input type="text" id="keywords-id" name="keywords" value="<?php echo Arr::get($_REQUEST, 'keywords') ?>" /></div>		<?php if ($error = Arr::get($errors, 'keywords')) : ?>			<div class="it-error"><?php echo $error ?></div>		<?php endif ?>	</div>    <div class="it-row">		<label for="description-id"><?php echo t('news.description'